  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'magento-1',
	title: 'Magento 1',
	sidebar_label: 'Magento 1'
};
export const metadata = {
  "unversionedId": "applications/magento-1",
  "id": "applications/magento-1",
  "isDocsHomePage": false,
  "title": "Magento 1",
  "description": "On this page, we explain step by step how to setup Magento 1 with CloudPanel.",
  "source": "@site/docs/applications/magento-1.md",
  "slug": "/applications/magento-1",
  "permalink": "/docs/v2/applications/magento-1",
  "editUrl": "https://github.com/cloudpanel-io/docs/tree/master/cloudpanel-ce/docs/applications/magento-1.md",
  "version": "current",
  "sidebar_label": "Magento 1"
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
export const rightToc = [{
  value: 'Installation',
  id: 'installation',
  children: [{
    value: 'Preparation',
    id: 'preparation',
    children: []
  }, {
    value: 'Installation',
    id: 'installation-1',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://magento.com/"
        }}>{`Magento 1`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup a `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://magento.com/"
        }}>{`Magento`}</a></strong>{` shop under the domain `}<strong parentName="p"><em parentName="strong"><a parentName="em" {...{
            "href": "http://www.domain.com"
          }}>{`www.domain.com`}</a></em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{`, a `}<a parentName="p" {...{
        "href": "../frontend-area/databases#adding-a-database"
      }}>{`Database`}</a>{`, and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Magento 1 Vhost Template`}</strong>{` and the right `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/magento-1/new_domain.png')} />
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`Magento`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go to the users `}<strong parentName="li">{`tmp`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd ~/tmp
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Download and extract the latest `}<strong parentName="li">{`Magento 1`}</strong>{` version.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`curl -sL https://github.com/cloudpanel-io/applications/blob/master/magento1/magento-1.9.4.5.tar.gz?raw=true | tar xfz -
`}</code></pre>
    <div {...{
      "className": "admonition admonition-caution alert alert--warning"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "16",
              "height": "16",
              "viewBox": "0 0 16 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M8.893 1.5c-.183-.31-.52-.5-.887-.5s-.703.19-.886.5L.138 13.499a.98.98 0 0 0 0 1.001c.193.31.53.501.886.501h13.964c.367 0 .704-.19.877-.5a1.03 1.03 0 0 0 .01-1.002L8.893 1.5zm.133 11.497H6.987v-2.003h2.039v2.003zm0-3.004H6.987V5.987h2.039v4.006z"
              }}></path></svg></span>{`Download Magento 1 Version`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`The latest version of `}<strong parentName="p">{`Magento 1`}</strong>{` can also be found and downloaded on the following site: `}<br />{` `}<a parentName="p" {...{
            "href": "https://magento.com/tech-resources/download"
          }}>{`https://magento.com/tech-resources/download`}</a></p></div></div>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Move files to the `}<strong parentName="li">{`htdocs`}</strong>{` directory of the domain:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cp -R magento/* /home/cloudpanel/htdocs/www.domain.com/
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Reset permissions.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
clpctl system:permissions:reset www.domain.com 775
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Clean up the `}<strong parentName="li">{`tmp`}</strong>{` directory.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf ~/tmp/*
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`Open your domain in the browser and go through the `}<strong parentName="li">{`Installation Wizard`}</strong>{`.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/magento-1/installation_wizard.png')} /> <br /> <br />
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol">{`Done!`}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
