  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'strapi-3',
	title: 'Strapi 3',
	sidebar_label: 'Strapi 3'
};
export const metadata = {
  "unversionedId": "applications/strapi-3",
  "id": "applications/strapi-3",
  "isDocsHomePage": false,
  "title": "Strapi 3",
  "description": "On this page, we explain step by step how to setup Strapi 3 with CloudPanel.",
  "source": "@site/docs/applications/strapi-3.md",
  "slug": "/applications/strapi-3",
  "permalink": "/docs/v1/applications/strapi-3",
  "editUrl": "https://github.com/cloudpanel-io/docs/tree/master/cloudpanel-ce/docs/applications/strapi-3.md",
  "version": "current",
