import useBaseUrl from '@docusaurus/useBaseUrl';
export const rightToc = [{
  value: 'Installation',
  id: 'installation',
  children: [{
    value: 'Preparation',
    id: 'preparation',
    children: []
  }, {
    value: 'Installation',
    id: 'installation-1',
    children: []
  }]
}, {
  value: 'Development',
  id: 'development',
  children: []
}, {
  value: 'Production Deployment',
  id: 'production-deployment',
  children: [{
    value: 'Setup Supervisor',
    id: 'setup-supervisor',
    children: []
  }, {
    value: 'Troubleshooting',
    id: 'troubleshooting',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://strapi.io/"
        }}>{`Strapi 3`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://strapi.io/"
        }}>{`Strapi`}</a></strong>{` under the subdomain `}<strong parentName="p"><em parentName="strong">{`api.domain.com`}</em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{` and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Strapi 3 Vhost Template`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/strapi-3/new_domain.png')} /> <br /><br />
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`Strapi`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol">{`In the first step, we need to downgrade (as `}<strong parentName="li">{`root`}</strong>{` user) `}<strong parentName="li">{`Node.js`}</strong>{` to version `}<strong parentName="li">{`12.x`}</strong>{`:`}</li>
    </ol>
    <p><strong parentName="p">{`Open the nodesource list file:`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`nano /etc/apt/sources.list.d/nodesource.list
`}</code></pre>
    <p><strong parentName="p">{`Replace:`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`deb https://deb.nodesource.com/node_14.x buster main
deb-src https://deb.nodesource.com/node_14.x buster main
`}</code></pre>
    <p><strong parentName="p">{`With:`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`deb https://deb.nodesource.com/node_12.x buster main
deb-src https://deb.nodesource.com/node_12.x buster main
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Update the `}<strong parentName="li">{`package sources`}</strong>{` and remove the old `}<strong parentName="li">{`Node.js`}</strong>{` version:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`apt update && dpkg -r --force-depends nodejs
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Install `}<strong parentName="li">{`Node.js`}</strong>{` Version `}<strong parentName="li">{`12.x`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`apt -y install nodejs
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go `}<strong parentName="li">{`htdocs`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Delete the project directory which has been created by `}<strong parentName="li">{`CloudPanel`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf /home/cloudpanel/htdocs/api.domain.com
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`We create the `}<strong parentName="li">{`Strapi`}</strong>{` app via `}<strong parentName="li">{`yarn`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`yarn create strapi-app api.domain.com --quickstart
`}</code></pre>
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol">{`Done! Open the `}<strong parentName="li">{`admin`}</strong>{` url in your browser to create an `}<strong parentName="li">{`admin user`}</strong>{`: `}<a parentName="li" {...{
          "href": "https://api.domain.com/admin"
        }}>{`https://api.domain.com/admin`}</a></li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/strapi-3/create_admin_user.png')} /> <br /><br />
    <h2 {...{
      "id": "development"
    }}>{`Development`}</h2>
    <p>{`For the development, you can start `}<strong parentName="p">{`Strapi`}</strong>{` from the commandline with a detailed output:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go the `}<strong parentName="li">{`project`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/api.domain.com/
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Start `}<strong parentName="li">{`Strapi`}</strong>{` via `}<strong parentName="li">{`yarn`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`yarn develop
`}</code></pre>
    <h2 {...{
      "id": "production-deployment"
    }}>{`Production Deployment`}</h2>
    <p>{`For running `}<strong parentName="p">{`Strapi`}</strong>{` in `}<strong parentName="p">{`production`}</strong>{` it's recommended to use a process control system like `}<a parentName="p" {...{
        "href": "http://supervisord.org/"
      }}>{`supervisor`}</a>{`,
which restarts the node processes automatically in case of a failure.`}</p>
    <h3 {...{
      "id": "setup-supervisor"
    }}>{`Setup Supervisor`}</h3>
    <ol>
      <li parentName="ol">{`Got to the `}<strong parentName="li">{`project directory`}</strong>{` and build `}<strong parentName="li">{`Strapi`}</strong>{` with user `}<strong parentName="li">{`john-ssh`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/api.domain.com/ && NODE_ENV=production yarn build
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Install the `}<strong parentName="li">{`supervisor`}</strong>{` package:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`apt update && apt -y install supervisor
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Create a `}<strong parentName="li">{`supervisor`}</strong>{` configuration file.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`touch /etc/supervisor/conf.d/strapi-api.conf
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Open the configuration file and put the following content:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`[program:strapi-api]
process_name=%(program_name)s_%(process_num)02d
environment=NODE_ENV=production
command=/usr/bin/yarn --cwd /home/cloudpanel/htdocs/api.domain.com/ start
numprocs=1
user=john-ssh
autostart=true
autorestart=true
stopsignal=KILL
stdout_logfile=/var/log/supervisor/strapi-api.log
`}</code></pre>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Attention`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure that the `}<strong parentName="p">{`user`}</strong>{` is correct to avoid permission problems.`}</p></div></div>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Update the configuration and start the node processes :`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`supervisorctl reread
supervisorctl update
supervisorctl start strapi-api:*
`}</code></pre>
    <p>{`With the command `}<strong parentName="p">{`supervisorctl`}</strong>{` you get information about the `}<strong parentName="p">{`status`}</strong>{`, `}<strong parentName="p">{`pid`}</strong>{`, and `}<strong parentName="p">{`uptime`}</strong>{`:`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/strapi-3/supervisorctl_screenshot.png')} /> 
    <h3 {...{
      "id": "troubleshooting"
    }}>{`Troubleshooting`}</h3>
    <p>{`You may want to see what happens in the background; you can do that by tailing the log file:`}</p>
    <pre><code parentName="pre" {...{}}>{`tail -f /var/log/supervisor/strapi-api.log -n1000
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
