  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'nodejs',
	title: 'Nodejs',
	sidebar_label: 'Nodejs'
};
export const metadata = {
  "unversionedId": "applications/nodejs",
  "id": "applications/nodejs",
  "isDocsHomePage": false,
  "title": "Nodejs",
  "description": "On this page, we explain step by step how to setup a Nodejs App with CloudPanel.",
  "source": "@site/docs/applications/nodejs.md",
  "slug": "/applications/nodejs",
  "permalink": "/docs/v2/applications/nodejs",
  "editUrl": "https://github.com/cloudpanel-io/docs/tree/master/cloudpanel-ce/docs/applications/nodejs.md",
  "version": "current",
  "sidebar_label": "Nodejs"
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
export const rightToc = [{
  value: 'Installation',
  id: 'installation',
  children: [{
    value: 'Preparation',
    id: 'preparation',
    children: []
  }, {
    value: 'Application Port',
    id: 'application-port',
    children: []
  }]
}, {
  value: 'Production Deployment',
  id: 'production-deployment',
  children: [{
    value: 'Setup Supervisor',
    id: 'setup-supervisor',
    children: []
  }, {
    value: 'Troubleshooting',
    id: 'troubleshooting',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup a `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://nodejs.org/"
        }}>{`Nodejs`}</a></strong>{` App with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example, we will setup a `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://nodejs.org/"
        }}>{`Nodejs`}</a></strong>{` under the domain `}<strong parentName="p"><em parentName="strong"><a parentName="em" {...{
            "href": "http://www.domain.com"
          }}>{`www.domain.com`}</a></em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{` and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Nodejs Vhost Template`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/nodejs/new_domain.png')} /> <br /><br />
    <h3 {...{
      "id": "application-port"
    }}>{`Application Port`}</h3>
    <p>{`By default, all requests are forwarded to port `}<strong parentName="p">{`3000`}</strong>{` via `}<strong parentName="p">{`reverse proxy`}</strong>{`.`}</p>
    <p>{`If your `}<strong parentName="p">{`Nodejs App`}</strong>{` is running under another `}<strong parentName="p">{`port`}</strong>{`, you can change it easily in the `}<a parentName="p" {...{
        "href": "../frontend-area/domains#vhost"
      }}>{`Vhost Editor`}</a>{` of your domain.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/nodejs/vhost_reverse_proxy_port.png')} />
    <h2 {...{
      "id": "production-deployment"
    }}>{`Production Deployment`}</h2>
    <p>{`For running a `}<strong parentName="p">{`NodeJs App`}</strong>{` in `}<strong parentName="p">{`production`}</strong>{` it's recommended to use a process control system like `}<a parentName="p" {...{
        "href": "http://supervisord.org/"
      }}>{`supervisor`}</a>{`,
which restarts the node processes automatically in case of a failure.`}</p>
    <h3 {...{
      "id": "setup-supervisor"
    }}>{`Setup Supervisor`}</h3>
    <ol>
      <li parentName="ol">{`Install the `}<strong parentName="li">{`supervisor`}</strong>{` package:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`apt update && apt -y install supervisor
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Create a `}<strong parentName="li">{`supervisor`}</strong>{` configuration file:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`touch /etc/supervisor/conf.d/nodejs-app.conf
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Open the configuration file and put the following content:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`[program:nodejs-app]
process_name=%(program_name)s_%(process_num)02d
command=/usr/bin/node /home/cloudpanel/htdocs/www.domain.com/app.js
numprocs=1
user=john-ssh
autostart=true
autorestart=true
stopsignal=KILL
stdout_logfile=/var/log/supervisor/nodejs-app.log
`}</code></pre>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Attention`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure that the `}<strong parentName="p">{`user`}</strong>{` is correct to avoid permission problems.`}</p></div></div>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Update the configuration and start the node processes:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`supervisorctl reread
supervisorctl update
supervisorctl start nodejs-app:*
`}</code></pre>
    <p>{`With the command `}<strong parentName="p">{`supervisorctl`}</strong>{` you get information about the `}<strong parentName="p">{`status`}</strong>{`, `}<strong parentName="p">{`pid`}</strong>{`, and `}<strong parentName="p">{`uptime`}</strong>{`:`}</p>
    <h3 {...{
      "id": "troubleshooting"
    }}>{`Troubleshooting`}</h3>
    <p>{`You may want to see what happens in the background; you can do that by tailing the log file:`}</p>
    <pre><code parentName="pre" {...{}}>{`tail -f /var/log/supervisor/nodejs-app.log -n1000
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
