const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://laravel.com/"
        }}>{`Laravel 8`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://laravel.com/"
        }}>{`Laravel`}</a></strong>{` under the domain `}<strong parentName="p"><em parentName="strong"><a parentName="em" {...{
            "href": "http://www.domain.com"
          }}>{`www.domain.com`}</a></em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{`, a `}<a parentName="p" {...{
        "href": "../frontend-area/databases#adding-a-database"
      }}>{`Database`}</a>{`, and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Laravel 8 Vhost Template`}</strong>{` and the right `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/laravel-8/new_domain.png')} /> <br /><br />
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Document Root`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure to point the `}<strong parentName="p">{`Document Root`}</strong>{` to the `}<strong parentName="p">{`public`}</strong>{` directory.`}</p></div></div>
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`Laravel`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go `}<strong parentName="li">{`htdocs`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Delete the project directory which has been created by `}<strong parentName="li">{`CloudPanel`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf /home/cloudpanel/htdocs/www.domain.com
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Install via `}<strong parentName="li">{`composer`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`php7.4 /usr/local/bin/composer create-project --prefer-dist laravel/laravel:^8 www.domain.com
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Reset permissions.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
clpctl system:permissions:reset www.domain.com 775
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Done! You can now open your domain in your browser to see the welcome page.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/laravel-8/welcome_to_laravel.png')} /> 
    <h2 {...{
      "id": "daemons"
    }}>{`Daemons`}</h2>
    <p>{`Powered by `}<a parentName="p" {...{
        "href": "http://supervisord.org/"
      }}>{`Supervisor`}</a>{`, daemons are used to keep long-running `}<strong parentName="p">{`PHP`}</strong>{` scripts alive.`}</p>
    <p>{`In case they will be terminated, `}<a parentName="p" {...{
        "href": "http://supervisord.org/"
      }}>{`Supervisor`}</a>{` makes sure to restart them automatically.`}</p>
    <h3 {...{
      "id": "installation-2"
    }}>{`Installation`}</h3>
    <ol>
      <li parentName="ol">
        <p parentName="li"><a parentName="p" {...{
            "href": "../frontend-area/users#ssh-login"
          }}>{`Login via SSH`}</a>{` to the server e.g. as `}<strong parentName="p">{`root`}</strong>{` user.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Install `}<strong parentName="p">{`supervisor`}</strong></p>
      </li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`apt install -y supervisor
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Create a `}<strong parentName="li">{`supervisor`}</strong>{` configuration file.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`touch /etc/supervisor/conf.d/supervisor.conf
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Open the configuration file and put the following content:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`[program:laravel-worker]
process_name=%(program_name)s_%(process_num)02d
command=php7.4 /home/cloudpanel/htdocs/www.domain.com/artisan queue:work redis --sleep=10 --daemon --quiet --queue="default"
numprocs=1
user=john-ssh
autostart=true
autorestart=true
stopsignal=KILL
stdout_logfile=/var/log/supervisor/laravel-worker.log
`}</code></pre>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Attention`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure that the `}<strong parentName="p">{`php version`}</strong>{` and the `}<strong parentName="p">{`user`}</strong>{` are correct to avoid permission problems.`}</p></div></div>
    <div {...{
      "className": "admonition admonition-tip alert alert--success"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.5 0C3.48 0 1 2.19 1 5c0 .92.55 2.25 1 3 1.34 2.25 1.78 2.78 2 4v1h5v-1c.22-1.22.66-1.75 2-4 .45-.75 1-2.08 1-3 0-2.81-2.48-5-5.5-5zm3.64 7.48c-.25.44-.47.8-.67 1.11-.86 1.41-1.25 2.06-1.45 3.23-.02.05-.02.11-.02.17H5c0-.06 0-.13-.02-.17-.2-1.17-.59-1.83-1.45-3.23-.2-.31-.42-.67-.67-1.11C2.44 6.78 2 5.65 2 5c0-2.2 2.02-4 4.5-4 1.22 0 2.36.42 3.22 1.19C10.55 2.94 11 3.94 11 5c0 .66-.44 1.78-.86 2.48zM4 14h5c-.23 1.14-1.3 2-2.5 2s-2.27-.86-2.5-2z"
              }}></path></svg></span>{`tip`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`For consuming messages in parallel, e.g., when you are working with `}<strong parentName="p">{`rabbitMQ`}</strong>{`, you may increase `}<strong parentName="p">{`numprocs`}</strong>{`.`}</p></div></div>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Update the configuration and start the worker.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`supervisorctl reread
supervisorctl update
supervisorctl start laravel-worker:*
`}</code></pre>
    <p>{`With the command `}<strong parentName="p">{`supervisorctl`}</strong>{` you get information about the `}<strong parentName="p">{`status`}</strong>{`, `}<strong parentName="p">{`pid`}</strong>{`, and `}<strong parentName="p">{`uptime`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/laravel-8/supervisorctl_screenshot.png')} /> 
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
