  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'building-extension',
	title: 'Building a PHP Extension',
	sidebar_label: 'Building a PHP Extension'
};
export const metadata = {
  "unversionedId": "guides/php/building-extension",
  "id": "guides/php/building-extension",
  "isDocsHomePage": false,
  "title": "Building a PHP Extension",
  "description": "On this site, you find a step-by-step guide on how to build a PHP Extension for a specific PHP Version.",
  "source": "@site/docs/guides/php/building-php-extension.md",
  "slug": "/guides/php/building-extension",
  "permalink": "/docs/cloudpanel-ce/guides/php/building-extension",
  "editUrl": "https://github.com/cloudpanel-io/docs/tree/master/cloudpanel-ce/docs/guides/php/building-php-extension.md",
