};
  /* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [{
  value: 'Building a PHP Extension',
  id: 'building-a-php-extension',
  children: []
}, {
  value: 'Testing',
  id: 'testing',
  children: [{
    value: 'CLI',
    id: 'cli',
    children: []
  }, {
    value: 'FPM',
    id: 'fpm',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this site, you find a `}<strong parentName="p">{`step-by-step guide`}</strong>{` on how to build a `}<strong parentName="p">{`PHP Extension`}</strong>{` for a specific `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <h2 {...{
      "id": "building-a-php-extension"
    }}>{`Building a PHP Extension`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Make a `}<strong parentName="p">{`backup`}</strong>{` of your server.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Install the `}<strong parentName="p">{`dev`}</strong>{` package for the `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
      </li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`apt update && apt install -y php7.4-dev
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Go to `}<a parentName="li" {...{
          "href": "https://pecl.php.net/"
        }}>{`https://pecl.php.net/`}</a>{` and search for the extension like `}<a parentName="li" {...{
          "href": "https://pecl.php.net/package/ssh2"
        }}>{`ssh2`}</a>{`:`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/guides/php/php-extensions/download_extension.png')} />
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Download the `}<strong parentName="li">{`tgz`}</strong>{` via `}<strong parentName="li">{`cURL`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`curl -O https://pecl.php.net/get/ssh2-1.2.tgz
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Extract the `}<strong parentName="li">{`tgz`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`tar xf ssh2-1.2.tgz
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol"><strong parentName="li">{`phpize`}</strong>{` the extension:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd ssh2-1.2
phpize7.4
`}</code></pre>
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol"><strong parentName="li">{`Compile`}</strong>{` and `}<strong parentName="li">{`build`}</strong>{` the extension:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`./configure
make
make install
`}</code></pre>
    <ol {...{
      "start": 8
    }}>
      <li parentName="ol">{`Register the extension for `}<strong parentName="li">{`CLI`}</strong>{` and `}<strong parentName="li">{`FPM`}</strong>{` in the `}<strong parentName="li">{`php.ini`}</strong>{`:`}</li>
    </ol>
    <p><strong parentName="p">{`CLI`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`nano /etc/php/7.4/cli/php.ini
`}</code></pre>
    <p><strong parentName="p">{`Add the following line at the end:`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`extension=ssh2.so
`}</code></pre>
    <p><strong parentName="p">{`FPM`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`nano /etc/php/7.4/fpm/php.ini
`}</code></pre>
    <p><strong parentName="p">{`Add the following line at the end:`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`extension=ssh2.so
`}</code></pre>
    <ol {...{
      "start": 9
    }}>
      <li parentName="ol">{`Restart the `}<strong parentName="li">{`PHP-FPM`}</strong>{` service:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`systemctl restart php7.4-fpm
`}</code></pre>
    <h2 {...{
      "id": "testing"
    }}>{`Testing`}</h2>
    <p>{`After registering the `}<strong parentName="p">{`PHP Extension`}</strong>{`, you can check if the extension is loaded for `}<strong parentName="p">{`CLI`}</strong>{` and `}<strong parentName="p">{`FPM`}</strong>{` correctly.`}</p>
    <h3 {...{
      "id": "cli"
    }}>{`CLI`}</h3>
    <p>{`You can use `}<strong parentName="p">{`grep`}</strong>{` to check if the extension is loaded:`}</p>
    <pre><code parentName="pre" {...{}}>{`php7.4 -m |grep 'ssh2' 
`}</code></pre>
    <p>{`If you don't get an output, then the extension is `}<strong parentName="p">{`NOT`}</strong>{` loaded.`}</p>
    <h3 {...{
      "id": "fpm"
    }}>{`FPM`}</h3>
    <p>{`To check if the extension is loaded for `}<strong parentName="p">{`FPM`}</strong>{`, you can check the `}<strong parentName="p">{`phpinfo`}</strong>{`.`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Create the file `}<strong parentName="p">{`t.php`}</strong>{` in the `}<strong parentName="p">{`root directory`}</strong>{`.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Put the following content in the file:`}</p>
      </li>
    </ol>
    <pre><code parentName="pre" {...{
        "className": "language-php"
      }}>{`<?php
phpinfo();
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Open `}<strong parentName="li">{`t.php`}</strong>{` in your browser and search for the `}<strong parentName="li">{`extension`}</strong>{`.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/guides/php/php-extensions/phpinfo_extension.png')} />
    <p>{`If you don't find information about the extension, then it's `}<strong parentName="p">{`NOT`}</strong>{` loaded.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
