  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'joomla-4',
	title: 'Joomla 4',
	sidebar_label: 'Joomla 4'
};
export const metadata = {
  "unversionedId": "applications/joomla-4",
  "id": "applications/joomla-4",
  "isDocsHomePage": false,
  "title": "Joomla 4",
  "description": "On this page, we explain step by step how to setup Joomla 4 with CloudPanel.",
  "source": "@site/docs/applications/joomla-4.md",
  "slug": "/applications/joomla-4",
  "permalink": "/docs/v2/applications/joomla-4",
  "editUrl": "https://github.com/cloudpanel-io/docs/tree/master/cloudpanel-ce/docs/applications/joomla-4.md",
  "version": "current",
  "sidebar_label": "Joomla 4"
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
export const rightToc = [{
  value: 'Installation',
  id: 'installation',
  children: [{
    value: 'Preparation',
    id: 'preparation',
    children: []
  }, {
    value: 'Installation',
    id: 'installation-1',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://www.joomla.org/"
        }}>{`Joomla 4`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://www.joomla.org/"
        }}>{`Joomla`}</a></strong>{` under the domain `}<strong parentName="p"><em parentName="strong"><a parentName="em" {...{
            "href": "http://www.domain.com"
          }}>{`www.domain.com`}</a></em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{`, a `}<a parentName="p" {...{
        "href": "../frontend-area/databases#adding-a-database"
      }}>{`Database`}</a>{`, and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Joomla 4 Vhost Template`}</strong>{` and the right `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/joomla-4/new_domain.png')} /> <br /><br />
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`Joomla`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go to the users `}<strong parentName="li">{`tmp`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd ~/tmp
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Download and extract the latest `}<strong parentName="li">{`Joomla 4`}</strong>{` version.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`mkdir ~/tmp/joomla-4 && curl -sL https://downloads.joomla.org/cms/joomla4/4-0-3/Joomla_4-0-3-Stable-Full_Package.tar.gz | tar xfz - -C ~/tmp/joomla-4/
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Move files to the `}<strong parentName="li">{`htdocs`}</strong>{` directory of the domain:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cp -R joomla-4/* /home/cloudpanel/htdocs/www.domain.com/
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Reset permissions.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
clpctl system:permissions:reset www.domain.com 775
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Clean up the `}<strong parentName="li">{`tmp`}</strong>{` directory.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf ~/tmp/*
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`Open your domain in the browser and go through the `}<strong parentName="li">{`installation wizard`}</strong>{`.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/joomla-4/installation_wizard.png')} />
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol">{`Done! `}<strong parentName="li">{`Joomla 4`}</strong>{` is now installed. `}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/joomla-4/joomla_admin.png')} />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
