const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://www.neos.io/"
        }}>{`Neos 7`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://www.neos.io/"
        }}>{`Neos`}</a></strong>{` under the domain `}<strong parentName="p"><em parentName="strong"><a parentName="em" {...{
            "href": "http://www.domain.com"
          }}>{`www.domain.com`}</a></em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{`, a `}<a parentName="p" {...{
        "href": "../frontend-area/databases#adding-a-database"
      }}>{`Database`}</a>{`, and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Neos 7 Vhost Template`}</strong>{` and the right `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/neos-7/new_domain.png')} /> <br /><br />
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Document Root`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure to point the `}<strong parentName="p">{`Document Root`}</strong>{` to the `}<strong parentName="p">{`Web`}</strong>{` directory.`}</p></div></div>
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`Neos`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go `}<strong parentName="li">{`htdocs`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Delete the project directory which has been created by `}<strong parentName="li">{`CloudPanel`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf /home/cloudpanel/htdocs/www.domain.com
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Install via `}<strong parentName="li">{`composer`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`php7.4 /usr/local/bin/composer create-project neos/neos-base-distribution:~7 www.domain.com
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Reset permissions.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
clpctl system:permissions:reset www.domain.com 775
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Open your domain in the browser and start the setup process: `}<a parentName="li" {...{
          "href": "https://www.domain.com/setup/"
        }}>{`https://www.domain.com/setup/`}</a></li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/neos-7/setup_password.png')} />
    <p>{`Enter the `}<strong parentName="p">{`Setup Password`}</strong>{` to start the installation process. `}</p>
    <p>{`The `}<strong parentName="p">{`Setup Password`}</strong>{` can be displayed with the following command:`}</p>
    <pre><code parentName="pre" {...{}}>{`cat /home/cloudpanel/htdocs/www.domain.com/Data/SetupPassword.txt
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`Go through all setup steps to finish the installation.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/neos-7/setup_steps.png')} />
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol">{`Done! `}<strong parentName="li">{`Neos`}</strong>{` is now installed.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/neos-7/welcome.jpg')} />
    </MDXLayout>;
}

;
