import useBaseUrl from '@docusaurus/useBaseUrl';
export const rightToc = [{
  value: 'CloudPanel Installation',
  id: 'cloudpanel-installation',
  children: [{
    value: 'SSH Login',
    id: 'ssh-login',
    children: []
  }, {
    value: 'Installer',
    id: 'installer',
    children: []
  }]
}, {
  value: 'Access CloudPanel',
  id: 'access-cloudpanel',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to run `}<strong parentName="p">{`CloudPanel`}</strong>{` on a dedicated server or any other cloud. `}<br />{`
For the installation, you need an empty server with `}<a parentName="p" {...{
        "href": "../requirements"
      }}>{`Debian 10 (Buster)`}</a>{` with root access.`}</p>
    <h2 {...{
      "id": "cloudpanel-installation"
    }}>{`CloudPanel Installation`}</h2>
    <h3 {...{
      "id": "ssh-login"
    }}>{`SSH Login`}</h3>
    <p>{`For installing the CloudPanel, we first need to login with `}<strong parentName="p">{`SSH`}</strong>{`.`}</p>
    <p>{`If you are using a `}<strong parentName="p">{`private key`}</strong>{` to login, the SSH command would be:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`ssh -i path_to_your_private_key root@serverIP
`}</code></pre>
    <p>{`If you are using a `}<strong parentName="p">{`password`}</strong>{` to login, the SSH command would be:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`ssh root@serverIP
`}</code></pre>
    <h3 {...{
      "id": "installer"
    }}>{`Installer`}</h3>
    <p>{`Before we run the installer, we need to update the sources and install required packages.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`apt update && apt -y upgrade && apt -y install curl wget sudo
`}</code></pre>
    <p>{`To start the `}<strong parentName="p">{`CloudPanel`}</strong>{` installation, execute the following command:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`curl -sSL https://installer.cloudpanel.io/ce/v1/install.sh | sudo bash
`}</code></pre>
    <h2 {...{
      "id": "access-cloudpanel"
    }}>{`Access CloudPanel`}</h2>
    <p>{`You can now access the CloudPanel via Browser: `}<strong parentName="p">{`https://serverIP:8443`}</strong></p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/ignore_self_signed_certificate.png')} />
    <p>{`Ignore the self-signed certificate warning and click on `}<strong parentName="p">{`Advanced`}</strong>{` and `}<strong parentName="p">{`Proceed`}</strong>{` to continue to CloudPanel.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
