import useBaseUrl from '@docusaurus/useBaseUrl';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, you will find a guide on how to run `}<strong parentName="p">{`CloudPanel`}</strong>{` under its own domain with a `}<strong parentName="p">{`Let's Encrypt certificate`}</strong>{`.`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../../frontend-area/domains#adding-a-domain"
        }}>{`Add the Domain`}</a>{` `}<strong parentName="li">{`cp.domain.com`}</strong>{` and select the `}<strong parentName="li">{`Generic Vhost Template`}</strong>{`.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/guides/cloudpanel/own-domain/new_domain.png')} />
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Go to the `}<a parentName="li" {...{
          "href": "../../frontend-area/domains#vhost"
        }}>{`Vhost Editor`}</a>{` of the `}<strong parentName="li">{`domain`}</strong>{` and replace the template with the following one:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`server {
  listen 80;
  listen [::]:80;
  listen 443 ssl http2;
  listen [::]:443 ssl http2;
  {{ssl_certificate_key}}
  {{ssl_certificate}}
  server_name cp.domain.com;
  {{root}}

  {{nginx_access_log}}
  {{nginx_error_log}}

  if ($bad_bot = 1) {
    return 403;
  }

  if ($scheme != "https") {
    rewrite ^ https://$host$uri permanent;
  }

  #if ($http_x_forwarded_proto != https) {
  #  return 301 https://$host$request_uri;
  #}

  #if ($http_x_forwarded_proto ~ https) {
  #  set $fastcgi_https on;
  #}

  location ~ /.well-known {
    auth_basic off;
    allow all;
  }

  location / {
    proxy_set_header Host $http_host; 
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Forwarded-For $remote_addr;
    proxy_set_header X-Forwarded-Host $http_host;
    proxy_pass https://127.0.0.1:8443/;
    proxy_max_temp_file_size 0;
    proxy_connect_timeout 7200;
    proxy_send_timeout 7200;
    proxy_read_timeout 7200;
    proxy_buffer_size 128k;
    proxy_buffers 4 256k;
    proxy_busy_buffers_size 256k;
    proxy_temp_file_write_size 256k;
 }
}
`}</code></pre>
    <p>{`This `}<strong parentName="p">{`Vhost Template`}</strong>{` is using a reverse proxy to forward all requests to the `}<strong parentName="p">{`CloudPanel`}</strong>{` port `}<strong parentName="p">{`8443`}</strong>{`.`}</p>
    <div {...{
      "className": "admonition admonition-caution alert alert--warning"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "16",
              "height": "16",
              "viewBox": "0 0 16 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M8.893 1.5c-.183-.31-.52-.5-.887-.5s-.703.19-.886.5L.138 13.499a.98.98 0 0 0 0 1.001c.193.31.53.501.886.501h13.964c.367 0 .704-.19.877-.5a1.03 1.03 0 0 0 .01-1.002L8.893 1.5zm.133 11.497H6.987v-2.003h2.039v2.003zm0-3.004H6.987V5.987h2.039v4.006z"
              }}></path></svg></span>{`caution`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure that your `}<strong parentName="p">{`domain`}</strong>{` is set as `}<strong parentName="p">{`server_name`}</strong>{`.`}</p></div></div>
    <p>{`If you are using `}<strong parentName="p">{`Cloudflare`}</strong>{`, comment the following lines in the `}<strong parentName="p">{`Vhost Template`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{}}>{`#if ($scheme != "https") {
#  rewrite ^ https://$host$uri permanent;
#}
`}</code></pre>
    <p>{`and uncomment the following ones:`}</p>
    <pre><code parentName="pre" {...{}}>{`if ($http_x_forwarded_proto != https) {
  return 301 https://$host$request_uri;
}

if ($http_x_forwarded_proto ~ https) {
  set $fastcgi_https on;
}
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">
        <p parentName="li">{`Issue a `}<a parentName="p" {...{
            "href": "../../frontend-area/domains#lets-encrypt"
          }}>{`Let's Encrypt Certificate`}</a>{` for your domain.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Done. `}<strong parentName="p">{`CloudPanel`}</strong>{` is now accessible via `}<inlineCode parentName="p">{`https://cp.domain.com`}</inlineCode></p>
      </li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
