const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to run `}<strong parentName="p">{`CloudPanel`}</strong>{` on `}<a parentName="p" {...{
        "href": "https://www.vultr.com/"
      }}>{`VULTR`}</a>{`.`}</p>
    <h2 {...{
      "id": "deploy-an-instance"
    }}>{`Deploy an Instance`}</h2>
    <p>{`1) Login into your `}<a parentName="p" {...{
        "href": "https://my.vultr.com/"
      }}>{`VULTR`}</a>{` account. `}<br /></p>
    <p>{`2) Click on the button `}<strong parentName="p">{`Deploy CloudPanel Instance`}</strong>{`.`}</p>
    <p><a parentName="p" {...{
        "href": "https://my.vultr.com/deploy?marketplace_app=cloudpanel1&marketplace_vendor_username=cloudpanel&ref=6879286"
      }}><img alt={"Deploy CloudPanel Instance"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!/mnt/PhpstormProjects/cloudpanel-docs/cloudpanel-ce/static/img/v1/getting_started/vultr_installation/create_cloudpanel_instance_one_click.png").default} /></a></p>
    <h3 {...{
      "id": "choose-server-type"
    }}>{`Choose Server Type`}</h3>
    <p>{`Choose the `}<strong parentName="p">{`Server Type`}</strong>{` you want to run the `}<strong parentName="p">{`Instance`}</strong>{` on.`}</p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/vultr_installation/choose_server_type_marketplace.png')} />
    <h3 {...{
      "id": "server-location"
    }}>{`Server Location`}</h3>
    <p>{`Choose the `}<strong parentName="p">{`Server Location`}</strong>{` where you want to run your `}<strong parentName="p">{`Instance`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/vultr_installation/select_server_location_marketplace.png')} />
    <h3 {...{
      "id": "server-size"
    }}>{`Server Size`}</h3>
    <p>{`Select the `}<strong parentName="p">{`Server Size`}</strong>{` you want to `}<strong parentName="p">{`deploy`}</strong>{`. We recommend at least `}<strong parentName="p">{`1 CPU`}</strong>{` and `}<strong parentName="p">{`2 GB of Memory`}</strong>{`. `}</p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/vultr_installation/choose_server_size_marketplace.png')} />
    <h3 {...{
      "id": "finalize-and-deploy"
    }}>{`Finalize and Deploy`}</h3>
    <p>{`Enter a `}<strong parentName="p">{`Server Hostname & Label`}</strong>{` like `}<strong parentName="p">{`cloudpanel`}</strong>{` and click on the button `}<strong parentName="p">{`Deploy Now`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/vultr_installation/enter_server_hostname_deploy.png')} /> <br /><br />
    <h2 {...{
      "id": "creating-a-firewall"
    }}>{`Creating a Firewall`}</h2>
    <p>{`For higher security, we recommend assigning a `}<strong parentName="p">{`Firewall`}</strong>{` to control the incoming traffic to your `}<strong parentName="p">{`Instance`}</strong>{`.`}</p>
    <p>{`1) Click in the left navigation on `}<strong parentName="p">{`Products`}</strong>{` and then on the tab `}<strong parentName="p">{`Firewall`}</strong>{`.`}</p>
    <p>{`2) Click on button `}<strong parentName="p">{`Add Firewall Group`}</strong>{`.`}</p>
    <p>{`3) Add a `}<strong parentName="p">{`Description`}</strong>{` like `}<strong parentName="p">{`CloudPanel`}</strong>{` and click on the button `}<strong parentName="p">{`Add Firewall Group`}</strong>{`.`}</p>
    <p>{`4) Open the ports `}<strong parentName="p">{`80 (HTTP)`}</strong>{` and `}<strong parentName="p">{`443 (HTTPS)`}</strong>{` for everyone.`}</p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/vultr_installation/add_firewall_group.png')} />
    <p>{`5) Click on `}<strong parentName="p">{`Linked Instances`}</strong>{` in the left menu and add your instance.`}</p>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Security`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`The port 22 (SSH) and 8443 (CloudPanel) should be restricted to your IPs only.`}</p></div></div>
    <h2 {...{
      "id": "access-cloudpanel"
    }}>{`Access CloudPanel`}</h2>
    <p>{`You can now access the CloudPanel via Browser: `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://yourFloatingIpAddress:8443"
        }}>{`https://yourFloatingIpAddress:8443`}</a></strong></p>
    <img class="border" src={useBaseUrl('img/v1/getting_started/ignore_self_signed_certificate.png')} />
    <p>{`Ignore the self-signed certificate warning and click on `}<strong parentName="p">{`Advanced`}</strong>{` and `}<strong parentName="p">{`Proceed`}</strong>{` to continue to CloudPanel.`}</p>
    </MDXLayout>;
}

;
