export const rightToc = [{
  value: 'Cloudflare Setup',
  id: 'cloudflare-setup',
  children: [{
    value: 'Name Servers',
    id: 'name-servers',
    children: []
  }, {
    value: 'DNS Settings',
    id: 'dns-settings',
    children: []
