  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'wordpress-5',
	title: 'WordPress 5',
	sidebar_label: 'WordPress 5'
};
export const metadata = {
  "unversionedId": "applications/wordpress-5",
  "id": "applications/wordpress-5",
  "isDocsHomePage": false,
  "title": "WordPress 5",
  "description": "On this page, we explain step by step how to setup WordPress 5 with CloudPanel.",
  "source": "@site/docs/applications/wordpress-5.md",
  "slug": "/applications/wordpress-5",
  "permalink": "/docs/cloudpanel-ce/applications/wordpress-5",
  "editUrl": "https://github.com/cloudpanel-io/docs/tree/master/cloudpanel-ce/docs/applications/wordpress-5.md",
  "version": "current",
  "sidebar_label": "WordPress 5",
  "sidebar": "sidebar",
