    "permalink": "/docs/cloudpanel-ce/applications/yii-2"
  }
};
  /* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
export const rightToc = [{
  value: 'Installation',
  id: 'installation',
  children: [{
    value: 'Preparation',
    id: 'preparation',
    children: []
  }, {
    value: 'Installation',
    id: 'installation-1',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://wordpress.org/"
        }}>{`WordPress 5`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://wordpress.org/"
        }}>{`WordPress`}</a></strong>{` under the domain `}<strong parentName="p"><em parentName="strong">{`www.domain.com`}</em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{`, a `}<a parentName="p" {...{
        "href": "../frontend-area/databases#adding-a-database"
      }}>{`Database`}</a>{`, and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`WordPress 5 Vhost Template`}</strong>{` and the right `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/wordpress-5/new_domain.png')} /> <br /><br />
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`WordPress`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go to the users `}<strong parentName="li">{`tmp`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd ~/tmp
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Download and extract the latest `}<strong parentName="li">{`WordPress 5`}</strong>{` version.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`curl -sL https://wordpress.org/latest.tar.gz | tar xfz -
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Move files to the `}<strong parentName="li">{`htdocs`}</strong>{` directory of the domain:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cp -R wordpress/* /home/cloudpanel/htdocs/www.domain.com/
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Reset permissions.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
clpctl system:permissions:reset www.domain.com 775
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Clean up the `}<strong parentName="li">{`tmp`}</strong>{` directory.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf ~/tmp/*
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`Open your domain in the browser and go through the `}<strong parentName="li">{`Installation Wizard`}</strong>{`.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/wordpress-5/installation_wizard.png')} /> <br /> <br />
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol">{`Open the `}<strong parentName="li">{`WordPress`}</strong>{` configuration file `}<strong parentName="li">{`wp-config.php`}</strong>{`.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`nano /home/cloudpanel/htdocs/www.domain.com/wp-config.php
`}</code></pre>
    <p>{`Add the following line after the other `}<strong parentName="p">{`constants`}</strong>{` to download all plugins and themes to the disk.`}</p>
    <pre><code parentName="pre" {...{}}>{`define('FS_METHOD','direct');
`}</code></pre>
    <img class="border" src={useBaseUrl('img/v1/applications/wordpress-5/fs_method_directory.png')} /> 
    <ol {...{
      "start": 8
    }}>
      <li parentName="ol">{`Done!`}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
