const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`On this page, we explain step by step how to setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://www.mautic.org/"
        }}>{`Mautic 4`}</a></strong>{` with `}<strong parentName="p">{`CloudPanel`}</strong>{`.`}</p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`In the following example we will setup `}<strong parentName="p"><a parentName="strong" {...{
          "href": "https://www.mautic.org/"
        }}>{`Mautic`}</a></strong>{` under the domain `}<strong parentName="p"><em parentName="strong">{`www.domain.com`}</em></strong>{`.`}</p>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <p>{`Before we can start with the installation, we need to create an `}<a parentName="p" {...{
        "href": "../frontend-area/users#adding-a-user"
      }}>{`SSH User`}</a>{`, a `}<a parentName="p" {...{
        "href": "../frontend-area/databases#adding-a-database"
      }}>{`Database`}</a>{`, and a `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Domain`}</a>{`.`}</p>
    <p>{`When you `}<a parentName="p" {...{
        "href": "../frontend-area/domains#adding-a-domain"
      }}>{`Add the Domain`}</a>{`, make sure to select the `}<strong parentName="p">{`Mautic 4 Vhost Template`}</strong>{` and the right `}<strong parentName="p">{`PHP Version`}</strong>{`.`}</p>
    <img class="border" src={useBaseUrl('img/v1/applications/mautic-4/new_domain.png')} /> <br /><br />
    <h3 {...{
      "id": "installation-1"
    }}>{`Installation`}</h3>
    <p>{`To install `}<strong parentName="p">{`Mautic`}</strong>{` do the following steps:`}</p>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "../frontend-area/users#ssh-login"
        }}>{`Login via SSH`}</a>{` to the server e.g. with `}<strong parentName="li">{`john-ssh`}</strong>{` and go to the `}<strong parentName="li">{`htdocs`}</strong>{` directory:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Delete the project directory which has been created by `}<strong parentName="li">{`CloudPanel`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`rm -rf /home/cloudpanel/htdocs/www.domain.com
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Clone the latest version from the `}<a parentName="li" {...{
          "href": "https://github.com/mautic/mautic"
        }}>{`Mautic Git Repository`}</a>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`git clone -b 4.0.0 https://github.com/mautic/mautic.git /home/cloudpanel/htdocs/www.domain.com
`}</code></pre>
    <div {...{
      "className": "admonition admonition-caution alert alert--warning"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "16",
              "height": "16",
              "viewBox": "0 0 16 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M8.893 1.5c-.183-.31-.52-.5-.887-.5s-.703.19-.886.5L.138 13.499a.98.98 0 0 0 0 1.001c.193.31.53.501.886.501h13.964c.367 0 .704-.19.877-.5a1.03 1.03 0 0 0 .01-1.002L8.893 1.5zm.133 11.497H6.987v-2.003h2.039v2.003zm0-3.004H6.987V5.987h2.039v4.006z"
              }}></path></svg></span>{`Latest Version`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`You find the latest version on github: `}<a parentName="p" {...{
            "href": "https://github.com/mautic/mautic/releases"
          }}>{`https://github.com/mautic/mautic/releases`}</a></p></div></div>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Install via `}<strong parentName="li">{`composer`}</strong>{`:`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/www.domain.com/
php7.4 /usr/local/bin/composer install
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Reset permissions.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`cd /home/cloudpanel/htdocs/
clpctl system:permissions:reset www.domain.com 775
`}</code></pre>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`Open your domain in the browser and go through the installation wizard.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/mautic-4/installation_wizard.png')} /> <br /><br />
    <ol {...{
      "start": 7
    }}>
      <li parentName="ol">{`Done! `}<strong parentName="li">{`Mautic 4`}</strong>{` is now installed.`}</li>
    </ol>
    <img class="border" src={useBaseUrl('img/v1/applications/mautic-4/admin_login.png')} />
    </MDXLayout>;
}

;
